package package1;

public class castingdemo01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=556;
		long y=x;
		System.out.println(y); /*<_----------here we have done casting*/
		
		short d=12;
		double g=d;
		System.out.println(g);
		
		char w='s';
		int a=w;
		System.out.println(a);
		
	}

}
